@font-face {
	font-family: 'DINcondensed';
	src: url('../fonts/DIN-Condensed-400.woff');
	font-weight: bold;
	font-style: normal;
}

@font-face {
	font-family: 'DINbold';
	src: url('../fonts/DIN-700.woff');
    font-weight: bold;
    font-style: normal;
}
*/
