	.stage article > * > div > div {-webkit-transform:translate3d( 0, 0, 0); position:relative; width:520px; padding:40px 60px 40px 50%;}
	.stage article > * > header {position:absolute; /*margin-top:-140px;*/ margin-top:-100px; z-index:4;}
