   .stage-preview {width:11%; margin:0; position:absolute; right:75px; top:105px; padding:0; z-index:80;}
   .stage-preview img {width:100%; display:inline;}
   .stage-preview li {border:2px solid #fff; margin: 0 0 7px; padding:0; background:#ffe600; list-style:none; cursor:pointer; position:relative; z-index:5;
      -webkit-transition: margin 0.2s ease;
      -moz-transition: margin 0.2s ease;
      transition: margin 0.2s ease;
      -webkit-box-shadow: 0 0px 5px #000;
         -moz-box-shadow: 0 0px 5px #000;
            box-shadow: 0 0px 5px #000;
   }
   .stage-preview li:hover {margin:-5px -25px; position:relative; z-index:7; border-color:#ffe600 !important;}
   .stage-preview li.act {position:relative; z-index:6; background:#fff; margin-left:-2px; margin-right:-2px; border-color:#ffe600;}
   .stage-preview li.act img {opacity:0.8}
   .stage-preview li + li {margin-top:-6px; z-index:4}
   .stage-preview li + li + li {z-index:3}
   .stage-preview li + li + li + li {z-index:2}
