#bvb-meta-bar .meta-bar__item-bigicon svg {width:1.5em; height:1.5em;}
#bvb-meta-bar .meta-bar__left svg {margin-right:0.5em;}

#bvb-meta-bar .meta-bar__items > * {float:left; display:block; overflow:hidden; position:relative; padding-top:1em; min-height:48px;}
#bvb-meta-bar .meta-bar__items > a {border-bottom: 6px solid transparent; padding:1em 0.5em 0.625em;}
