#bvb-meta-bar a:hover svg, #bvb-meta-bar .current.current svg {fill:#ffe600;}

#bvb-meta-bar .visually-hidden {position: absolute !important; top: -9999px !important; left: -9999px !important;}

#bvb-meta-bar .meta-bar__toggle { border-radius: 0 0 3em 3em; cursor: pointer; height: 1.5em; left: 50%; margin-left: -1.5em; margin-top: -0.5em; padding-top: 3px; position: absolute; text-align: center; top: 100%; width: 3em; background:inherit; border:1px solid rgba(255, 255, 255, 0.2); }
#bvb-meta-bar .meta-bar__toggle:before {content:''; background:inherit; display:block; width:120%; height:8px; position:absolute; left:-2px; top:-1px;}
#bvb-meta-bar.responsive-opened .meta-bar__toggle > svg {transform: rotate(180deg); transform-origin: 50% 50%;}
#bvb-meta-bar .meta-bar__toggle:hover svg {fill:#ffe600;}
#bvb-meta-bar .meta-bar__content {overflow:hidden; max-height:200px; transition:0.3s linear;}
#bvb-meta-bar.done-animating .meta-bar__content {overflow:visible;}

#bvb-meta-bar .meta-bar__evonik > svg {width:107px; height:24px;}
#bvb-meta-bar .meta-bar__evonik:hover svg {fill:#fff;}
#bvb-meta-bar .meta-bar__einsundeins > svg {width:30px; height:30px;}
#bvb-meta-bar .meta-bar__einsundeins:hover svg {fill:#fff;}
