/*
 * @name wowBook
 * @desc style for wowBook plugin for jQuery
 *
 * @author Marcio Aguiar
 * @version 1.3.7
 *
 * Date: Wed Dec 8 10:05:49 2010 -0200
 */

.wowbook {
    position: relative;
    zoom: 1;
    -ms-text-size-adjust: auto;
    -webkit-text-size-adjust: auto;
}

/ .wowbook *,
.wowbook-container * {
    -moz-transition: none !important;
    -webkit-transition: none !important;
    transition: none !important;
}

.wowbook-origin {
    z-index: 1;
}

.wowbook-page {
    zoom: 1;
}

.wowbook-page-content {
    position: absolute;
    overflow: hidden;
    background: white;
    zoom: 1;
    -webkit-backface-visibility: hidden;
}

.wowbook-loading .wowbook-page-content {
    background-image: url(./loading.gif);
    background-position: 50% 50%;
    background-repeat: no-repeat;
}

.wowbook-loading .wowbook-zoomcontent {
    opacity: 0;
}

.wowbook-zoomcontent {
    opacity: 1;
    -webkit-transition: opacity 0.5s ease-in;
    -moz-transition: opacity 0.5s ease-in;
    -o-transition: opacity 0.5s ease-in;
    -ms-transition: opacity 0.5s ease-in;
    transition: opacity 0.5s ease-in;
}

.wowbook.wowbook-loading {
    background-image: url(./loading.gif);
    background-position: 50% 50%;
    background-repeat: no-repeat;
}

img.wowbook-lazy {
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
}

.wowbook-fullscreen-fix {
    height: 100% !important;
    width: 100% !important;
}


/* marker class */

.wowbook-left {}

.wowbook-right {}

.wowbook-disabled {}

.wowbook-handle {
    cursor: pointer;
    height: 100%;
    position: absolute;
    top: 0;
    width: 50px;
    z-index: 100001;
    /* without this Chrome will mess the zindex of hardcover when it flips, making
	   the cover to jump when the "curl page corner on hover" is enabled */
    -webkit-transform: translate3d(0px, 0px, 10000px);
}

.wowbook-handle.wowbook-disabled {
    display: none;
}

.wowbook-unselectable {
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
}

.wowbook-page-content.wowbook-double-page {
    padding: 0;
}


/* hard page edge */

.wowbook-hardpage {
    -moz-transform-style: preserve-3d;
    -o-transform-style: preserve-3d;
    -webkit-transform-style: preserve-3d;
    -ms-transform-style: preserve-3d;
    transform-style: preserve-3d;
}

.wowbook-3d-border {
    background: #eee;
    height: 100%;
    left: 100%;
    position: absolute;
    top: 0;
    width: 5px;
    -moz-transform: rotateY(90deg);
    -o-transform: rotateY(90deg);
    -webkit-transform: rotateY(90deg);
    -ms-transform: rotateY(90deg);
    transform: rotateY(90deg);
    -moz-transform-origin: 0 0 0;
    -o-transform-origin: 0 0 0;
    -webkit-transform-origin: 0 0 0;
    -ms-transform-origin: 0 0 0;
    transform-origin: 0 0 0;
}

.wowbook-left .wowbook-3d-border {
    left: 0;
}


/* shadows */

.wowbook-shadow-clipper {
    position: absolute;
    overflow: hidden;
    z-index: 999;
    zoom: 1;
    -webkit-backface-visibility: hidden;
    /* eliminate jagged edges */
}

.wowbook-shadow-container {
    display: none;
    position: absolute;
    z-index: 999;
    zoom: 1;
}

.wowbook-shadow-internal {
    background: url(./shadow_internal_fold.png) repeat-y;
    height: 500px;
    width: 200px;
    position: absolute;
    zoom: 1;
    -webkit-backface-visibility: hidden;
    /* eliminate jagged edges */
}

.wowbook-shadow-fold {
    background: url(./shadow_fold.png) right top repeat-y;
    height: 500px;
    width: 100px;
    position: absolute;
    zoom: 1;
    -webkit-backface-visibility: hidden;
    /* eliminate jagged edges */
}

.wowbook-shadow-fold-flipped {
    background-image: url(./shadow_fold_flip_h.png);
}

.wowbook-fold-gradient {
    background: url(./fold_gradient.png) right top repeat-y;
    height: 500px;
    width: 125px;
    position: absolute;
    top: 0;
    zoom: 1;
    -webkit-backface-visibility: hidden;
    /* eliminate jagged edges */
}

.wowbook-fold-gradient-flipped {
    background-image: url(./fold_gradient_flip_h.png);
}

.wowbook-fold-gradient-container {
    position: absolute;
    z-index: 1001;
}

.wowbook-gutter-shadow {
    height: 100%;
    width: 40px;
    position: absolute;
    top: 0;
    z-index: 1;
    pointer-events: none;
}

.wowbook-left .wowbook-gutter-shadow {
    background: url(./gutter_shadow_left.png) right top repeat-y;
    right: 0;
    width: 70px;
}

.wowbook-right .wowbook-gutter-shadow {
    background: url(./gutter_shadow_right.png) right top repeat-y;
    left: 0;
}

.wowbook-hard-page-shadow {
    background: rgba(0, 0, 0, 0.2);
    left: 0;
    height: 100%;
    position: absolute;
    top: 0;
    width: 100%;
    transform: translatez(0);
    -webkit-transform: translatez(0);
}

.wowbook-hard-page-dropshadow {
    background: #888;
    box-shadow: 0 0 7px 0px #666;
    background: rgba(96, 96, 96, 0.5);
    position: absolute;
    top: 0;
    z-index: 999;
    transform: translatez(0);
    -webkit-transform: translatez(0);
}

.wowbook-book-shadow {
    -moz-box-shadow: 0 0 7px #666;
    -ms-box-shadow: 0 0 7px #666;
    -webkit-box-shadow: 0 0 7px #666;
    -o-box-shadow: 0 0 7px #666;
    box-shadow: 0 0 7px #666;
    /* if you change this, change the .wowbook-hard-page-dropshadow too */
}


/* this should avoid a faint gray line in shadows element on android */

@media screen and (-webkit-min-device-pixel-ratio:0) {
    .wowbook-shadow-internal,
    .wowbook-gutter-shadow,
    .wowbook-fold-gradient,
    .wowbook-shadow-fold {
        image-rendering: pixelated;
    }
}

.wowbook-page-number {
    bottom: 0;
    padding-bottom: 0.3em;
    position: absolute;
    z-index: 1;
}

.wowbook-left .wowbook-page-number {
    left: 0;
    padding-left: 0.5em;
}

.wowbook-right .wowbook-page-number {
    right: 0;
    padding-right: 0.5em;
}

.wowbook-page-holded {}

.wowbook-page-holded object {
    /* visibility: hidden; */
}


/* Thumbnails */

.wowbook-thumbnails {
    background: none;
    left: 0px;
    top: 0px;
    position: absolute;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    image-rendering: optimizeQuality;
    z-index: 10000;
}

.wowbook-thumbnails .wowbook-clipper {
    overflow: hidden;
    position: relative;
    -webkit-overflow-scrolling: touch;
}

.wowbook-thumbnails ul {
    position: relative;
    margin: 0;
    padding: 0;
}

.wowbook-thumbnails .wowbook-button {
    background: none no-repeat 50% 50% #333;
    cursor: pointer;
    font-family: wowbook-toolbar-fonts;
    left: 0;
    position: absolute;
    top: 0;
    text-align: center;
}

.wowbook-thumbnail {
    cursor: pointer;
    display: inline-block;
    *display: inline;
    /* for IE7 */
    zoom: 1;
    /* for IE7 */
    overflow: hidden;
    position: relative;
    vertical-align: top;
}

.wowbook-thumbnail.wowbook-thumbnail-custom {
    background-color: white;
    background-size: 100% 100%;
}

.wowbook-thumbnail-custom.wowbook-loading {
    background-image: url(./loading.gif);
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: auto;
}

.wowbook-thumbnails .wowbook-button:before {
    content: "";
    display: inline-block;
    height: 100%;
    vertical-align: middle;
    width: 1px;
}

.wowbook-thumbnails .wowbook-button:after {
    display: inline-block;
    vertical-align: middle;
}


/* BUGFIX. IE8 apparently ignores background-position
   when zoom<>1 is applied and the element has position:absolute */

.wowbook-thumbnail .wowbook-page-content {
    position: relative;
}

.wowbook-thumbnail.wowbook-right {
    margin-right: 1em;
}

.wowbook-overlay {
    left: 0;
    height: 100%;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: 100000;
}


/* Thumbnails - horizontal */

.wowbook-horizontal.wowbook-thumbnails {
    width: 100%;
}

.wowbook-horizontal.wowbook-thumbnails .wowbook-clipper {
    height: 100%;
    position: relative;
    margin: 0 30px;
    /* margin is the width of the buttons */
}

.wowbook-horizontal.wowbook-thumbnails ul {
    display: inline-block;
    *display: inline;
    /* IE7 */
    *zoom: 1;
    /* IE7 */
    white-space: nowrap;
}

.wowbook-horizontal.wowbook-thumbnails .wowbook-button {
    width: 30px;
    min-width: unset;
    height: 100%;
    text-align: center;
}

.wowbook-horizontal.wowbook-thumbnails .wowbook-button:after {
    content: "\f053";
    /* fa-chevron-left */
}

.wowbook-horizontal.wowbook-thumbnails .wowbook-next.wowbook-button:after {
    content: "\f054";
    /* fa-chevron-right */
}

.wowbook-horizontal.wowbook-thumbnails .wowbook-next.wowbook-button {
    left: auto;
    right: 0;
}

.wowbook-horizontal .wowbook-thumbnail {
    /* without this, the "nowrap" value set in the "ul" will
	   be inherited and (probably)	mess with your thumbnails */
    white-space: normal;
    /* BUT IE7 NEED THIS */
    *white-space: nowrap;
}

.wowbook-horizontal .wowbook-thumbnail .wowbook-page-content {
    /* AGAIN, IE7 NEED THIS */
    *white-space: normal;
}


/* Thumbnails - vertical */

.wowbook-vertical.wowbook-thumbnails {
    display: inline-block;
    height: 100%;
}

.wowbook-vertical.wowbook-thumbnails .wowbook-clipper {
    position: absolute;
    top: 30px;
    bottom: 30px;
    /* this value is the height of the buttons */
}

.wowbook-vertical.wowbook-thumbnails .wowbook-button {
    height: 30px;
    line-height: 30px;
    width: 100%;
}

.wowbook-vertical.wowbook-thumbnails .wowbook-button:after {
    vertical-align: baseline;
}

.wowbook-vertical.wowbook-thumbnails .wowbook-next.wowbook-button {
    bottom: 0;
    top: auto;
}

.wowbook-vertical.wowbook-thumbnails .wowbook-button:after {
    content: "\f077";
    /* fa-chevron-up */
}

.wowbook-vertical.wowbook-thumbnails .wowbook-next.wowbook-button:after {
    content: "\f078";
    /* fa-chevron-down */
}

.wowbook-vertical .wowbook-thumbnail {
    margin-bottom: 5px;
    margin-top: 5px;
}

.wowbook-vertical .wowbook-thumbnail.wowbook-right {
    margin-right: 0;
}

.wowbook-vertical.wowbook-closable .wowbook-thumbnail:first-child {
    margin-left: 50%;
}


/*
	Cursor
*/

.wowbook-draggable {
    cursor: hand;
    cursor: grab;
    cursor: -moz-grab;
    cursor: -webkit-grab;
    -moz-user-select: none;
}

.wowbook-draggable.wowbook-dragging {
    cursor: grabbing;
    cursor: -moz-grabbing;
    cursor: -webkit-grabbing;
}


/*
	PDF
*/

.wowbook-pdf .wowbook-page-content {
    padding: 0;
}

.wowbook-pdf .wowbook-page-content canvas {
    max-width: none !important;
}

.wowbook-pdf-annotations {
    position: absolute;
    top: 0;
    left: 0;
    width: 0;
    height: 0;
    z-index: 2;
    /* must be above the pdf-text div*/
}

.wowbook-pdf .annotLink {
    padding: 0;
}

.wowbook-pdf .annotLink>a {
    font-size: 1em;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
}

.wowbook-pdf .annotLink>a:hover {
    background: none repeat scroll 0 0 #ff0;
    box-shadow: 0 2px 10px #ff0;
    opacity: 0.2;
}

.wowbook-pdf .linkAnnotation {
    position: absolute;
    padding: 0;
}

.wowbook-pdf .linkAnnotation>a {
    font-size: 1em;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
}

.wowbook-pdf .linkAnnotation>a:hover {
    background: #ff0 none repeat scroll 0 0;
    box-shadow: 0 2px 10px #ff0;
    opacity: 0.2;
}

.wowbook-pdf-text {
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    overflow: hidden;
    opacity: 0.25;
    line-height: 1.0;
    z-index: 1;
}

.wowbook-pdf-text>div {
    color: transparent;
    position: absolute;
    white-space: pre;
    cursor: text;
    -webkit-transform-origin: 0% 0%;
    -moz-transform-origin: 0% 0%;
    -o-transform-origin: 0% 0%;
    -ms-transform-origin: 0% 0%;
    transform-origin: 0% 0%;
}

.wowbook-pdf-text .highlight {
    margin: -1px;
    padding: 1px;
    background-color: rgb(180, 0, 170);
    border-radius: 4px;
}

.wowbook-pdf-text .highlight.begin {
    border-radius: 4px 0px 0px 4px;
}

.wowbook-pdf-text .highlight.end {
    border-radius: 0px 4px 4px 0px;
}

.wowbook-pdf-text .highlight.middle {
    border-radius: 0px;
}

.wowbook-pdf-text .highlight.selected {
    background-color: rgb(0, 100, 0);
}

.wowbook-pdf-text ::selection {
    background: rgb(0, 0, 255);
    color: transparent;
}

.wowbook-pdf-text ::-moz-selection {
    background: rgb(0, 0, 255);
    color: transparent;
}

.wowbook-pdf-text .endOfContent {
    display: block;
    position: absolute;
    left: 0px;
    top: 100%;
    right: 0px;
    bottom: 0px;
    z-index: -1;
    cursor: default;
    -webkit-user-select: none;
    -ms-user-select: none;
    -moz-user-select: none;
}

.wowbook-pdf-text .endOfContent.active {
    top: 0px;
}


/*
	Table of contents
*/

.wowbook-toc {
    background: white;
    display: inline-block;
    overflow-y: auto;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    z-index: 10001;
    width: 250px;
