css:
.sravvpl_statsOverlayContainer {
  position: absolute;
  left: 0;
  width: 100%;
  height: 100%;

  z-index: 1;
}

.sravvpl_errorOverlayContainer {
  position: absolute;
  width: 100%;
  height: 100%;

  /* z-index: 1; */
}

.sravvpl_labelHorizontalContainer {
  position: absolute;
  top: 20%;
  left: 30%;
}

.sravvpl_posterOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;
}

.sravvpl_adPosterOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;
}

.sravvpl_shareOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  z-index: 6;
}

.sravvpl_multiviewOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  z-index: 6;
}

.sravvpl_watermarkContainer {
  width: 100%;
}

.sravvpl_uiElement {
  bottom: 0;
  outline: none;
}

.sravvpl_endOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  color: white;

  display: flex;
  align-items: center;
  justify-content: center;

  z-index: 6;
}

.sravvpl_availableStreamsOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  color: white;

  display: flex;
  align-items: center;
  justify-content: center;

  z-index: 6;
}

.sravvpl_correlatedStreamsOverlayContainer {
  position: absolute;
  bottom: 0;
  left: 0;

  width: 100%;
  height: 100%;

  color: white;

  display: flex;
  align-items: center;
  justify-content: center;

  z-index: 6;
}

.sravvpl_fullscreen {
  cursor: auto;
}

.sravvpl_fullscreenNormal {
  width: 100%!important;
  height: 100%!important;
}

.sravvpl_fullscreenNormal.sravvpl_autohide {
  cursor: none;
}

.sravvpl_loadingAnimationContainer {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -50px;
  margin-left: -50px;
}

.sravvpl_theme_1 .sravvpl_loadingAnimationContainer {
  margin-left: -40px;
}

.sravvpl_leftControlsContainer {
  position: absolute;
  left: 0;
}

.sravvpl_hide {
  visibility: hidden!important;
  opacity: 0!important;
}

.sravvpl_controlbar-left {
  transition: left .5s;
}

.sravvpl_autohide .sravvpl_controlbar-left {
  /* display: none; */
  left: -100px;
  transition: left .5s;
}

.sravvpl_autohide .sravvpl_controlbar {
  opacity: 0;
  transition: opacity .5s;
}

.sravvpl_autohide .sravvpl_settingsContainer {
  opacity: 0;
  transition: opacity .5s;
}

.sravvpl_autohide .sravvpl_ccOverlayContainer {
  opacity: 0;
  transition: opacity .5s;
}

.sravvpl_autohide .sravvpl_multiAudioOverlay {
  opacity: 0;
  transition: opacity .5s;
}

.sravvpl_spoilerAlertContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  z-index: 1;
}

.sravvpl_trackProgressContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  z-index: 1;
}

.sravvpl_statsUnavailableContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  z-index: 1;
}

.sravvpl_settingsContainer {
  position: absolute;

  z-index: 2;
  background-color: rgba(0, 0, 0, 0.8) !important;
}

.sravvpl_theme_1 .sravvpl_settingsContainer {
  position: absolute;

  z-index: 2;
  background-color: #1a1a1a !important;
}

.sravvpl_ccOverlayContainer {
  position: absolute;
  background-color: rgba(0, 0, 0, 0.8) !important;
  z-index: 2;
}

.sravvpl_streamTitleContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: auto;
}

.sravvpl_autohide .sravvpl_streamTitleContainer {
  opacity: 0;
  transition: opacity .5s;
}

.sravvpl_chromecastOverlay {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;

  background-color: black;
  color: white;
  text-align: center;
  padding-top: 10px;

  z-index: 1;
}

.sravvpl_premiumOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.sravvpl_multiAudioOverlayContainer {
  position: absolute;
  top: 0;
  left: 0;

  width: 100%;
  height: 100%;
}

.sravvpl_dualview .sravvpl_subtitles {
  display: none;
}

.sravvpl_quadview .sravvpl_subtitles {
  display: none;
}

.sravvpl_overlayview .sravvpl_subtitles {
  display: none;
}

.sravvpl_livelikeChatHolder {
  display: flex;

  width: 40%;
  float: right;
  height: 80%;
  flex-direction: column-reverse;
}

.sravvpl_livelikeChatWrapper {
  border: 1px solid grey;
  background: lightslategrey;
  border-width: medium;
  color: #000000;
  border-radius: 10px;
  padding: 5px;
}
svg {
  pointer-events: none;
}

.sravvpl_timeline-bar-indicator-event {
  background-color: #000000;
}

.sravvpl_uiContainer {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  fill: #ffffff;
  color: #ffffff;
  overflow: hidden;
}

div[class*="size-"] .sravvpl_uiContainer .sravvpl_uiElement {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
}

.sravvpl_controlbar-left-buttons {
  background-color: rgba(0, 0, 0, 0.5);
}

.sravvpl_controlbar {
  background-color: rgba(0, 0, 0, 0.5);
}

.sravvpl_timeline-bar-background {
  background-color: #43473a;
}
.sravvpl_timeline-bar-loading {
  background-color: #b5b7b2;
}

.sravvpl_timeline-bar-playing {
}

.sravvpl_timeline-dvr {
}

.sravvpl_controls-button:hover .sravvpl_toolTipWrapper {
  display: block !important;
}

.sravvpl_controls-button-container {

}

.sravvpl_controls-button:focus {
  outline: none;
}

.sravvpl_controls-button-active {
}

.sravvpl_volume-bar-background {
  background-color: #43473a;
}

.sravvpl_volume-bar-active {
}
.sravvpl_volume-bar-knob {
}

.sravvpl_timeline-bar-tooltip {
  background-color: rgba(0, 0, 0, 0.5);
  white-space: nowrap;
}

.sravvpl_timeline-icon-hover {
  background-color: rgba(0, 0, 0, 0.5);
}

.sravvpl_controlbar-left {
  position: absolute;
  margin-top: auto;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  width: 40px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-content: center;
  transition: left 0.5s;

  z-index: 1;
}

.sravvpl_controlbar {
  position: absolute;
  bottom: 0;
  width: 100%;
  height: auto;
  -ms-user-select: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  user-select: none;

  opacity: 1;
  transition: opacity 0.2s;

  z-index: 1;
}

.sravvpl_timeline {
  position: relative;
  width: 100%;
}

.sravvpl_timeline-open {
  height: 56px;
  transition: height 0.4s;
}

.sravvpl_timeline-close {
  height: 30px;
  transition: height 0.4s;
}

.sravvpl_timeline-close .sravvpl_timeline-bar-indicator {
  opacity: 0;
  transition: opacity 0.2s;
}

.sravvpl_timeline-icons {
  position: absolute;
  width: 96%;
  height: 30px;
  margin-left: 2%;
  bottom: 8px;
}

.sravvpl_timeline-event {
  position: absolute;
  height: 30px;
  width: 30px;
  text-align: center;
  margin-left: -15px;
}

.sravvpl_timeline-bars {
  position: absolute;
  width: 96%;
  height: 3px;
  left: 2%;
  bottom: 13px;
  transition: 0.3s;
}

.sravvpl_timeline-bars:hover {
  height: 5px;
}

.sravvpl_timeline-bars.sravvpl_useOtherSvgs {
  height: 2px;
  transition: 0.3s;
}

.sravvpl_useOtherSvgs {}

.sravvpl_timeline-bars.sravvpl_useOtherSvgs:hover {
  height: 4px;
}

.sravvpl_timeline-bar-knob {
  position: absolute;
  width: 14px;
  height: 14px;
  margin-top: -5px;
  margin-left: -8px;
  border-radius: 50%;
}

.sravvpl_size-small .sravvpl_timeline-bar-knob,
.sravvpl_size-tiny .sravvpl_timeline-bar-knob {
  width: 10px;
  height: 10px;
  margin-top: -3px;
}

.sravvpl_timeline-bars.sravvpl_useOtherSvgs .sravvpl_timeline-bar-knob {
  width: 6px;
  height: 6px;
  margin-top: -2px;
  margin-left: -4px;
}

.sravvpl_timeline-bars.sravvpl_useOtherSvgs:hover .sravvpl_timeline-bar-knob {
  width: 12px;
  height: 12px;
  margin-top: -4px;
}

.sravvpl_timeline-bar-indicator-event {
  width: 4px;
  height: 3px;
  position: absolute;
  bottom: 0;
  margin-left: -2px;
  transition: 0.3s;
}

.sravvpl_timeline-bars:hover .sravvpl_timeline-bar-indicator-event {
  height: 5px;
}

.sravvpl_timeline-bar-indicator-event-icon {
  position: absolute;
  width: 16px;
  height: 18px;
  margin-left: -6px;
  margin-top: -30px;
  cursor: pointer;
}

.sravvpl_timeline-bar-indicator-event-icon svg {
  width: 100%;
  height: 100%;
}

.sravvpl_timeline-bar-indicator-event-icon-plus {
  position: absolute;
  width: 12px;
  height: 12px;
  top: -3px;
  right: -3px;
  /* margin-right: -10px;
	margin-top: -36px; */
}

.sravvpl_timeline-icon-fadeout {
  opacity: 0.4;
}

.sravvpl_timeline-bar-indicator-other-events {
  /*display: block;*/
  position: absolute;
  top: -65px;
  left: -12px;
  background-color: rgba(0, 0, 0, 0.5);
  /* height: auto; */
  height: 24px;
  /*
  width: max-content;
	width: -moz-max-content;
	width: -webkit-max-content;
  box-sizing: unset;
  */
  width: auto;
  white-space: nowrap;
}

.sravvpl_timeline-bar-indicator-other-events-icon {
  position: relative;
  width: 14px;
  height: 14px;
  /* float: left; */
  padding: 5px;
  box-sizing: unset;
  display: inline-block;
}

.sravvpl_timeline-bar-background {
  position: absolute;
  width: 100%;
  height: 100%;
}

.sravvpl_timeline-bar-loading {
  position: absolute;
  height: 100%;
}

.sravvpl_timeline-bar-playing {
  position: absolute;
  height: 100%;
}

.sravvpl_timeline-bar-indicator {
  position: absolute;
  width: 100%;
  height: 100%;
  opacity: 1;
  transition: opacity 0.2s;
}

.sravvpl_controlbuttons {
  position: relative;
  width: 100%;
  height: 40px;
}

.sravvpl_controlbuttons-left {
  float: left;
  margin-left: 5px;
}

.sravvpl_controlbuttons-right {
  float: right;
  margin-right: 5px;
}
.sravvpl_controls-button {
  float: left;
  width: 40px;
  height: 40px;
  cursor: pointer;
}

.sravvpl_controls-button svg {
  width: 50%;
  height: 50%;
  margin-left: 25%;
  margin-top: 25%;
}

.sravvpl_controls-button img {
  width: 50%;
  height: 50%;
  margin-left: 25%;
  margin-top: 25%;
}

/** OUTLINE CHROMECAST TEST **/
#sravvpl_chromecast-button {
  border: none;
  background-color: transparent;
  padding: 0;
}

#sravvpl_chromecast-button svg {
  width: 60%;
  height: 60%;
  margin-left: 0%;
  margin-top: 5%;
}

.sravvpl_controls-button i,
.sravvpl_controls-button span {
  vertical-align: middle;
  display: inline-block;
  width: 50%;
  height: 50%;
  margin-left: 25%;
  margin-top: 25%;
}

.sravvpl_controls-button-disable {
  opacity: 0.5;
  cursor: default !important;
  width: 40px;
  height: 40px;
}

.sravvpl_controls-button-disable svg {
  width: 50%;
  height: 50%;
  margin-left: 25%;
  margin-top: 25%;
}

.sravvpl_controls-button-disable i {
  vertical-align: middle;
  display: inline-block;
  width: 50%;
  height: 50%;
  margin-left: 25%;
  margin-top: 25%;
}

.sravvpl_controls-volume {
  position: relative;
  opacity: 0;
  display: inline-block;
  width: 0;
  height: 40px;
  line-height: 40px;
  text-align: center;
  margin: 0;
  overflow: hidden;
  transition: 0.25s;
  /*transition: margin .2s cubic-bezier(0.4,0.0,1,1),width .2s cubic-bezier(0.4,0.0,1,1);*/
}

.sravvpl_button-volume:hover + .sravvpl_controls-volume,
.sravvpl_button-volume:focus + .sravvpl_controls-volume {
  opacity: 1;
  width: 100px;
  /*transition: margin .2s cubic-bezier(0.0,0.0,0.2,1),width .2s cubic-bezier(0.0,0.0,0.2,1);*/
}

.sravvpl_controls-volume:hover,
.sravvpl_controls-volume:focus {
  opacity: 1;
  width: 100px;
}

.sravvpl_controls-text {
  float: left;
  width: auto;
  height: 40px;
  padding-left: 5px;
  padding-right: 5px;
  line-height: 40px;
  font-size: 14px;
}

.sravvpl_volume-bars {
  position: absolute;
  width: 80px;
  margin-left: 5%;
  height: 4px;
  top: 50%;
  margin-top: -2px;
}

.sravvpl_volume-bar-background {
  position: absolute;
  width: 100%;
  height: 100%;
}

.sravvpl_volume-bar-active {
  position: absolute;
  height: 100%;
}

.sravvpl_volume-bar-hidden {
  position: absolute;
  width: 100%;
  height: 20px;
  margin-top: -8px;
  cursor: pointer;
}

.sravvpl_volume-bar-knob {
  position: absolute;
  width: 10px;
  height: 10px;
  margin-top: -3px;
  margin-left: -4px;
  border-radius: 50%;
}

.sravvpl_timeline-bar-tooltip {
  display: none;
  position: absolute;
  text-align: center;
  border-radius: 5px;
  font-size: 14px;

  padding: 6px;
  margin-top: -40px;

  transform: translate(-50%);
  pointer-events: none;
}

.sravvpl_timeline-icon-hover {
  display: block;
  position: absolute;
  text-align: center;
  border-radius: 5px;
  font-size: 15px;
  font-family: 'Core Sans Regular', sans-serif !important;
  padding: 8px;
  margin-top: -115px;

  transform: translate(-50%);
  pointer-events: none;

  max-width: 400px;
}

.sravvpl_timeline-icon-hover.sravvpl_timeline-pre-newline {
  margin-top: -130px;
}


.sravvpl_timeline-icon-hover.sravvpl_timeline-pre-newline pre {
  white-space: pre;
}

.sravvpl_timeline-icon-hover.sravvpl_timeline-pre-ellipsis pre {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.sravvpl_timeline-bars.sravvpl_useOtherSvgs .sravvpl_timeline-bar-tooltip {
  font-size: 15px;
  border-radius: 2px;
  padding: 5px;
  margin-top: -35px;
  background: #1a1a1a;
  z-index: 1;
}

.sravvpl_timeline-bar-tooltip-others {
  display: block;
  position: absolute;
  font-size: 15px;
  margin-top: -30px;
  pointer-events: none;
  font-family: 'TNeo-ExtraBold', Arial, sans-serif;
}

.sravvpl_timeline-bar-hidden {
  position: absolute;
  width: 100%;
  height: 18px;
  margin-top: -6px;
  cursor: pointer;
}

.sravvpl_timeline-bar-hidden:hover + .sravvpl_timeline-bar-tooltip,
.sravvpl_timeline-bar-knob:hover + .sravvpl_timeline-bar-tooltip {
  display: block;
}

.sravvpl_timeline-bar-tooltip-others {
  display: block;
}

.sravvpl_timeline-bar-tooltip-none {
  display: none;
}

@supports (-webkit-overflow-scrolling: touch) {
  .sravvpl_timeline-bar-hidden:hover + .sravvpl_timeline-bar-tooltip {
    display: none;
  }

  .sravvpl_timeline-bar-tooltip-others {
    display: block;
  }
}

.sravvpl_timeline-disable-left {
  position: absolute;
  height: 100%;
  background: #262626 !important;
  cursor: auto;
}

.sravvpl_timeline-disable-right {
  position: absolute;
  height: 100%;
  background: #262626 !important;
  cursor: auto;
  right: 0;
}

.sravvpl_livestream-dot::before {
  color: red;
  content: "\2022";
  float: left;
  padding-right: 5px;
}

.sravvpl_livestream-backtolive-dot::before {
  color: rgb(255, 255, 255);
  cursor: pointer;
  content: "\2022";
  float: left;
  padding-right: 5px;
}

.sravvpl_theme_3 .sravvpl_livestream-dot::before {
  content: "";
  background-color: #EC123F;
  float: left;
  border: 2px solid #FFFFFF;
  height: 16px;
  width: 16px;
  border-radius: 50%;
  transform: translateY(70%);
  margin-right: 5px;
  padding-right: 0px;
  box-sizing: border-box;
}

.sravvpl_theme_3 .sravvpl_livestream-backtolive-dot::before {
  content: "";
  background-color: grey;
  cursor: pointer;
  float: left;
  border: 2px solid #FFFFFF;
  height: 16px;
  width: 16px;
  border-radius: 50%;
  transform: translateY(70%);
  margin-right: 5px;
  padding-right: 0px;
  box-sizing: border-box;
}

.sravvpl_vod,
.sravvpl_live {
  width: 80px !important;
}

.sravvpl_wrapper {
  background-color: #111;
  width: 100%;
  height: 100%;
  position: relative;
}

.sravvpl_videoContainer {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0px;
  left: 0px;
}

.sravvpl_imgElement {
  position: absolute;
  width: 100%;
  height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  opacity: 0.7;
}

.sravvpl_adContainer0 {
  position: absolute;
  width: 100%;
  height: 100%;
  overflow: hidden;
}

.sravvpl_adContainer1 {
  position: absolute;
  width: 100%;
  height: 100%;
}

.sravvpl_adContainer2 {
  position: absolute;
  width: 100%;
  height: 100%;
}

.sravvpl_adContainer3 {
  position: absolute;
  width: 100%;
  height: 100%;
}

.sravvpl_srplayer {
  position: absolute;
  width: 100%;
  height: 100%;
}

.sravvpl_splitScreenLeft {
  width: 50% !important;
  height: 50% !important;
  left: 0;
}

.sravvpl_splitScreenRight {
  width: 50% !important;
  height: 50% !important;
  right: 0;
  bottom: 0;
  position: absolute;
}

.sravvpl_displayNone {
  display: none !important;
}

.sravvpl_thumbnails {
  position: absolute;
  margin-left: -80px;
  width: 160px;
  height: 90px;
  margin-top: -70px;
  background-color: black;
  overflow: hidden;
}

.sravvpl_icon-premium-button {
  color: #666;
  font-weight: 700;
  font-style: italic;
}

.sravvpl_icon-premium-button div {
  margin-top: 25%;
  margin-left: 25%;
}

.sravvpl_icon-premium-button.sravvpl_premium-long-text {
  color: #fff !important;
}

.sravvpl_icon-premium-button:hover {
  color: #fff !important;
}

/********************************* THEME 0 ***********************/
@media (pointer: fine) {
  .sravvpl_theme_0 .sravvpl_timeline-bar-knob {
    visibility: hidden;
  }
  .sravvpl_theme_0 .sravvpl_timeline-bar-knob {
    transform: scale(0);
    transition: transform 0.3s;
  }
  .sravvpl_theme_0 .sravvpl_timeline-bars:hover .sravvpl_timeline-bar-knob {
    visibility: visible;
    transform: scale(1);
  }
}

.sravvpl_theme_0 .sravvpl_uiContainer {
  font-family: 'Core Sans Regular', sans-serif;
}

.sravvpl_theme_0 * {
  box-sizing: unset;
}

.sravvpl_theme_0 .sravvpl_icon-premium-button.sravvpl_controls-button.sravvpl_premium-long-text {
  width: 70px !important;
}

.sravvpl_theme_0 .sravvpl_icon-premium-button.sravvpl_controls-button.sravvpl_premium-long-text div {
  width: 100%;
  text-align: center;
  margin-left: 0px;
  margin-top: 15%;
}

.sravvpl_theme_0 .sravvpl_controlbuttons-left {
  display: flex;
  flex-direction: row;
}

.sravvpl_theme_0 .sravvpl_controlbuttons-right {
  display: flex;
  flex-direction: row;
}

