.usercentrics-button .usercentrics-button .checkbox-inline input.intermediate[type="checkbox"]::after, .usercentrics-button .usercentrics-button .checkbox input.intermediate[type="checkbox"]::after, .usercentrics-button .usercentrics-button input.intermediate[type="checkbox"]::after { border: none !important; }
