text:
.not-ready .site-header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 60px;
    z-index: 2;
    -webkit-transition: all .5s ease-in-out;
    -o-transition: all .5s ease-in-out;
    transition: all .5s ease-in-out;
}
.not-ready .header-logo {
    display: block;
    background-color: #004b9c;
    width: 60px;
    height: 60px;
    padding: 15px;
    margin: 0;
}
.not-ready .header-search,
.not-ready .header-login,
.not-ready .header-live {
    display: none;
}
.not-ready .module-image {
    display: none;
}

#vc_newsletter_subscribe input[type=checkbox] {
    display: inline-block;
    margin: 0 5px 5px 0;
    width: auto;
}
#vc_newsletter_subscribe input[type=email] {
    margin-top: 10px;
}
