#cookie-bar {
  text-align: center;
  padding: 5px 0;
}
#cookie-bar.fixed {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
}
#cookie-bar.fixed.bottom {
  bottom: 0;
  top: auto;
}
#cookie-bar p {
  margin: 0;
  padding: 0 10px;
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
  display: block;
  padding-right: 50px;
  position: relative;
  box-sizing: border-box;
  text-align: left;
}
#cookie-bar p a.link {
  margin: 0;
}
#cookie-bar .cb-enable,
#cookie-bar .cb-disable,
#cookie-bar .cb-policy {
  color: #ffffff;
  display: inline-block;
  text-transform: uppercase;
  text-decoration: none;
  padding: 0 6px;
  margin-left: 8px;
}
#cookie-bar .cb-enable {
  margin-left: 15px;
  float: right;
  position: absolute;
  top: 50%;
  margin-top: -12px;
  right: 10px;
}
#cookie-bar .cb-disable:hover {
  background: #bb0000;
}
#cookie-bar .cb-policy:hover {
  background: #0055dd;
}
