}
/* DUAL */
.l1pVideoDual #l1pVideo0 {
  display: block;
  width: 48.5%;
  height: 48.5%;
  left: 1%;
  top: 25%;
  transition: all 0.3s;
}
.l1pVideoDual #l1pVideo1 {
  display: block;
