}
.l1pVideoDual #l1pVideo3 {
  display: none;
}
/* QUAD */
.l1pVideoQuad #l1pVideo0 {
  display: block;
  width: 48.5%;
  height: 48.5%;
  left: 1%;
  top: 1%;
  transition: all 0.3s;
}
.l1pVideoQuad #l1pVideo1 {
  display: block;
  width: 48.5%;
  height: 48.5%;
  left: 50.5%;
  top: 1%;
  transition: all 0.3s;
}
.l1pVideoQuad #l1pVideo2 {
  display: block;
  width: 48.5%;
  height: 48.5%;
  left: 1%;
  top: 50.5%;
  transition: all 0.3s;
}
.l1pVideoQuad #l1pVideo3 {
  display: block;
  width: 48.5%;
  height: 48.5%;
  left: 50.5%;
  top: 50.5%;
  transition: all 0.3s;
}
/* OVERLAY */
.l1pVideoOverlay #l1pVideo0 {
  display: block;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  transition: all 0.3s;
}
.l1pVideoOverlay #l1pVideo1 {
  display: block;
  width: 25%;
  height: 25%;
  right: 15px;
  top: 15px;
  transition: all 0.3s;
}
.l1pVideoOverlay #l1pVideo2 {
