/* default styles for extension "tx_brasportmedia" */
    textarea.f3-form-error {
        background-color:#FF9F9F;
        border: 1px #FF0000 solid;
    }

    input.f3-form-error {
        background-color:#FF9F9F;
        border: 1px #FF0000 solid;
    }

    .tx-bra-sportmedia table {
        border-collapse:separate;
        border-spacing:10px;
    }

    .tx-bra-sportmedia table th {
        font-weight:bold;
    }

    .tx-bra-sportmedia table td {
        vertical-align:top;
    }

    .typo3-messages .message-error {
        color:red;
    }

    .typo3-messages .message-ok {
        color:green;
    }

