  0% {
    transform: rotate(-1deg); }
  20% {
    opacity: 0.7;
    transform: rotate(1deg); }
  40% {
    transform: rotate(-1deg); }
  60% {
    opacity: 1;
    transform: rotate(1deg) scaleY(1.04); }
  80% {
    transform: rotate(-2deg) scaleY(0.92); }
  100% {
    transform: rotate(1deg); } }

