/* This is a separate CSS file because our fonts are hosted on woobox.com and must use relative file paths because of CORS issues */
@font-face {
	font-family: 'icons';
	src:url('/fonts/app/icons.eot');
	src:url('/fonts/app/icons.eot') format('embedded-opentype'),
		url('/fonts/app/icons.woff') format('woff'),
		url('/fonts/app/icons.ttf') format('truetype'),
		url('/fonts/app/icons.svg') format('svg');
	font-weight: normal;
	font-style: normal;
}

.fade-enter-active { animation: 0.5s ease 0s 1 normal none running fade-in; }
.fade-leave-active { animation: 0.5s ease 0s 1 normal none running fade-out; }
.bounce-enter-active { animation: 0.8s ease 0s 1 normal none running bounce-in; }
.bounce-leave-active { animation: 0.5s ease 0s 1 normal none running bounce-out; }
@-webkit-keyframes fade-in { 
  0% { opacity: 0; }
  100% { opacity: 1; }
}
@keyframes fade-in { 
  0% { opacity: 0; }
  100% { opacity: 1; }
}
@-webkit-keyframes fade-out { 
  0% { opacity: 1; }
  100% { opacity: 0; }
}
@keyframes fade-out { 
  0% { opacity: 1; }
  100% { opacity: 0; }
}
@-webkit-keyframes bounce-in { 
  0% { transform: scale(0); }
  50% { transform: scale(1.5); }
  100% { transform: scale(1); }
}
@keyframes bounce-in { 
  0% { transform: scale(0); }
  50% { transform: scale(1.5); }
  100% { transform: scale(1); }
}
@-webkit-keyframes bounce-out { 
  0% { transform: scale(1); }
  50% { transform: scale(1.5); }
  100% { transform: scale(0); }
}
@keyframes bounce-out { 
  0% { transform: scale(1); }
  50% { transform: scale(1.5); }
  100% { transform: scale(0); }
}
