.button, .icon-button:hover { cursor: pointer; }
.button { display: inline-flex; flex-direction: row; justify-content: center; align-items: center; padding: 0px 16px; height: var(--button_height--small,40px); font-family: var(--primary_font,"Effra",sans-serif); font-size: 12px; line-height: 1; border-radius: var(--button_radius,24px); text-decoration: none; white-space: nowrap; background: transparent; border: 0px; outline: none; transition-property: color, background-color, box-shadow; transition-duration: 0.2s; transition-timing-function: ease-out; }
@media screen and (min-width: 1024px) {
  .button { padding: 0px 16px; height: var(--button_height--large,48px); font-size: 16px; }
  .button--small { padding: 0px 16px; height: 40px; font-size: 14px; }
}
.button__icon { position: relative; top: 1px; height: 1em !important; width: 1em !important; }
.button__icon--left { margin-right: 0.5em; }
.button__icon--right { margin-left: 0.5em; }
.button--primary-light { color: rgb(255, 255, 255); background-color: rgb(103, 14, 54); box-shadow: rgb(103, 14, 54) 0px 0px 0px 0px; }
.button--primary-light:hover { box-shadow: rgb(103, 14, 54) 0px 0px 0px 4px; }
