.button--primary-dark:hover { box-shadow: rgb(255, 255, 255) 0px 0px 0px 4px; }
.button--ghost-light { color: rgb(103, 14, 54); background-color: transparent; border: 2px solid rgb(103, 14, 54); }
.button--ghost-light:hover { color: rgb(255, 255, 255); background-color: rgb(103, 14, 54); }
.button--ghost-dark { color: rgb(255, 255, 255); background-color: transparent; border: 2px solid rgb(255, 255, 255); }
.button--ghost-dark:hover { color: rgb(103, 14, 54); background-color: rgb(255, 255, 255); }
.button--label-light { padding: 0px; color: rgb(103, 14, 54); text-decoration: none; background-color: transparent; border: 0px; }
.button--label-light:hover { text-decoration: underline; }
.button--label-dark { color: rgb(255, 255, 255); text-decoration: none; background-color: transparent; border: 0px; }
.button--label-dark:hover { text-decoration: underline; }
.button--inline { padding-left: 0px; }
