}
@media screen and (min-width: 1024px) {
  .wysiwyg { font-size: var(--body_text_size--large,20px); }
}
.wysiwyg.rtl { direction: rtl; text-align: right; unicode-bidi: bidi-override; }
.wysiwyg .button--label-dark, .wysiwyg .button--label-light { padding: 0px; }
.wysiwyg .button { margin-bottom: 1em; }
.wysiwyg p { margin-bottom: 1.5em; font-size: inherit; }
.wysiwyg p.small { font-size: 0.8em; }
.wysiwyg a:not(.button) { color: rgb(103, 14, 54); text-decoration: underline; }
.wysiwyg a:not(.button):hover { text-decoration: none; }
.is-dark .wysiwyg a:not(.button) { color: inherit; }
.wysiwyg .ql-size-large { font-size: 1.5rem; font-weight: 700; }
.wysiwyg hr { margin: 40px 0px; }
.wysiwyg blockquote, .wysiwyg blockquote p { font-family: var(--primary_font,"Effra",sans-serif); font-size: 1.2em; text-transform: none; text-align: center; font-weight: 700; line-height: 1.3; margin: 1em 0.5em; }
.is-light .wysiwyg blockquote, .is-light .wysiwyg blockquote p { color: rgb(103, 14, 54); }
.is-dark .wysiwyg blockquote, .is-dark .wysiwyg blockquote p { color: inherit; }
.wysiwyg ol { margin: 1em 0px; padding: 0px 0px 0px 1em; list-style: none; counter-reset: li 0; }
.wysiwyg ol li { font-size: 1em; margin-bottom: 1em; position: relative; counter-increment: li 1; }
.wysiwyg ol li::before { content: counter(li) "."; display: inline-block; width: 1em; margin-left: -1.5em; margin-right: 0.5em; color: var(--body_text_color--light-mode,#212121); font-weight: inherit; font-family: var(--primary_font,"Effra",sans-serif); text-align: right; }
.is-dark .wysiwyg ol li::before { color: var(--body_text_color--dark-mode,#fff); }
.wysiwyg ul { margin: 1em 0px; padding: 0px 0px 0px 1em; list-style: none; counter-reset: li 0; }
.wysiwyg ul li { margin: 0px 0px 1em; font-size: 1em; position: relative; }
.wysiwyg ul li::before { content: ""; position: absolute; width: 8px; height: 8px; background-color: var(--body_text_color--light-mode,#212121); border-radius: 50%; top: 0.5em; left: -1em; }
.is-dark .wysiwyg ul li::before { background-color: var(--body_text_color--dark-mode,#fff); }
.wysiwyg .table-wrapper { overflow-x: auto; }
.wysiwyg table { margin-bottom: 1em; width: 100%; text-align: left; border-collapse: collapse; }
.wysiwyg thead tr { color: rgb(255, 255, 255); background-color: rgb(103, 14, 54); }
.is-dark .wysiwyg thead tr { color: rgb(103, 14, 54); background-color: rgb(255, 255, 255); }
.wysiwyg th { padding: 0.5em; color: rgb(255, 255, 255); font-weight: 700; background-color: rgb(103, 14, 54); border: 1px solid rgb(255, 255, 255); }
.is-dark .wysiwyg th { color: rgb(103, 14, 54); border: 1px solid rgb(33, 33, 33); }
.is-dark .wysiwyg th, .wysiwyg tr:nth-child(2n+1) td { background-color: rgb(255, 255, 255); }
.is-dark .wysiwyg tr:nth-child(2n+1) td { background-color: rgba(242, 245, 250, 0.2); }
.wysiwyg tr:nth-child(2n) td { background-color: rgb(255, 255, 255); }
.is-dark .wysiwyg tr:nth-child(2n) td { background-color: rgba(250, 251, 253, 0.1); }
.wysiwyg td { padding: 0.5em; font-family: var(--primary_font,"Effra",sans-serif); font-size: 0.8em; font-weight: 400; border: 1px solid rgb(170, 170, 170); }
.is-dark .wysiwyg td { color: rgb(255, 255, 255); border: 1px solid rgb(33, 33, 33); }
.wysiwyg td > b, .wysiwyg td > strong { font-family: var(--primary_font,"Effra",sans-serif); font-size: 1em; text-transform: uppercase; }
.wysiwyg img { display: block; width: 100%; height: auto; max-width: 640px; margin: 1em auto; }
.wysiwyg > :last-child { padding-bottom: 0px; margin-bottom: 0px; }
.radial-overlay { position: relative; }
.radial-overlay::before { content: ""; position: absolute; bottom: 0px; left: 0px; height: 100%; width: 100%; background: linear-gradient(transparent 25%, rgba(0, 0, 0, 0.45) 50%, rgb(0, 0, 0)); z-index: 3; transition: height 0.4s ease-out 0s; opacity: 1; }
@media screen and (min-width: 1024px) {
  .radial-overlay::before { background: radial-gradient(at center top, transparent, rgba(0, 0, 0, 0.3) 40%, rgb(0, 0, 0)); }
}
.radial-overlay::after { content: ""; position: absolute; top: 0px; right: 0px; left: 0px; background-image: linear-gradient(rgba(0, 0, 0, 0.7), transparent); height: 176px; z-index: 2; }
@media screen and (min-width: 760px) {
  .radial-overlay::after { height: 192px; }
}
.linear-overlay { position: relative; }
.linear-overlay::before { content: ""; position: absolute; top: 0px; right: 0px; bottom: 0px; left: 0px; background: linear-gradient(transparent 25%, rgba(0, 0, 0, 0.3) 50%, rgba(0, 0, 0, 0.8)); z-index: 2; }
.highlight-block { display: flex; flex-direction: column; justify-content: flex-start; align-items: stretch; width: 100%; max-width: 600px; margin: 0px auto; padding: 8px 0px 0px; background: rgb(255, 255, 255); border-top: 1px solid rgba(255, 255, 255, 0.2); }
@media screen and (min-width: 1024px) {
  .highlight-block { padding: 8px 32px 0px; border-top: 0px; border-left: 1px solid rgba(255, 255, 255, 0.2); }
}
.highlight-block:first-child { border: 0px; }
.highlight-block__loader { margin: auto; color: var(--accent_color--light-mode,#670e36); }
.highlight-block__header { display: flex; flex-direction: row; justify-content: space-between; align-items: center; height: 32px; margin-bottom: 8px; }
.highlight-block__label { color: rgb(170, 170, 170); font-family: var(--primary_font,"Effra",sans-serif); font-size: 12px; font-weight: var(--body_text_weight,400); text-transform: none; }
.highlight-block__footer { display: flex; flex-direction: row; justify-content: center; align-items: center; margin: auto 0px 0px; padding: 16px 0px; }
.highlight-block__button { margin: 0px 16px; }
