div#sliding-popup, div#sliding-popup .eu-cookie-withdraw-banner, .eu-cookie-withdraw-tab { background: rgb(0, 47, 104); }
div#sliding-popup.eu-cookie-withdraw-wrapper { background: transparent; }
#sliding-popup h1, #sliding-popup h2, #sliding-popup h3, #sliding-popup p, #sliding-popup label, #sliding-popup div, .eu-cookie-compliance-more-button, .eu-cookie-compliance-secondary-button, .eu-cookie-withdraw-tab { color: rgb(255, 255, 255); }
.eu-cookie-withdraw-tab { border-color: rgb(255, 255, 255); }

.dialogue__space { display: flex; position: fixed; width: 100vw; left: 0px; right: 0px; bottom: 0px; z-index: 999; align-items: center; justify-content: center; }
.dialogue__mid { max-width: 1440px; width: 100%; display: flex; flex-direction: row-reverse; }
.dialogue__snippet { -webkit-font-smoothing: antialiased; transform: translate3d(105%, 0px, 0px); height: auto; bottom: 0px; font-size: 16px; line-height: 22px; opacity: 0; display: none; color: rgb(34, 36, 36); z-index: 99999; }
.dialogue__snippet a { text-decoration: none; }
a.dialogue__clickArea { position: absolute; top: 0px; bottom: 0px; left: 0px; right: 0px; }
.dialogue__overlay, .dialogue__overlay__mini, .dialogue__overlay__micro { flex-direction: column; align-items: center; margin: 1em; text-align: center; border-radius: 4px; box-shadow: rgba(0, 0, 0, 0.14) 0px 4px 5px 0px, rgba(0, 0, 0, 0.12) 0px 1px 10px 0px, rgba(0, 0, 0, 0.2) 0px 2px 4px -1px; background-color: rgb(254, 254, 254); }
.dialogue__overlay img, .dialogue__overlay__mini img { width: 240px; margin-top: -96px; }
.dialogue__overlay__mini img { width: 200px; margin-top: -72px; }
.dialogue__overlay .dialogue__content { width: 320px; padding: 24px; }
.dialogue__overlay__mini .dialogue__content, .dialogue__overlay__micro .dialogue__content { width: 280px; padding: 24px; }
.dialogue__title { font-size: 18px; line-height: 1.4; margin-top: 0px; margin-bottom: 8px; }
.dialogue__desc { font-size: 14px; line-height: 1.5; padding-bottom: 8px; }
.dialogue__interstitial { display: none; align-items: flex-end; width: 100%; height: 100%; left: 0px; right: 0px; position: fixed; background: rgba(0, 0, 0, 0.5); padding: 0px; z-index: 999; overflow: hidden !important; }
.dialogue__interstitial .dialogue__content { width: 100%; background: rgb(249, 249, 249); }
.dialogue__interstitial_size { width: 360px; padding: 3em 0px; margin: auto; text-align: center; }
.dialogue__banner { align-items: center; flex-direction: row-reverse; justify-content: space-between; right: 0px; left: 0px; margin: 0px; padding: 0px 2em; width: 100%; height: 80px; background: rgb(254, 254, 254); box-shadow: rgba(0, 0, 0, 0.14) 0px 4px 5px 0px, rgba(0, 0, 0, 0.12) 0px 1px 10px 0px, rgba(0, 0, 0, 0.2) 0px 2px 4px -1px; }
.dialogue__banner img { width: 240px; margin-top: 0em; }
.dialogue__banner > .dialogue__content { max-width: 100%; }
.dialogue__banner .dialogue__title { margin-bottom: 0px; font-size: 18px; letter-spacing: 0px; }
.dialogue__banner > .dialogue__buttons > a { margin-bottom: 0px; }
bottom > .dialogue__content { max-width: 100%; }
.dialogue__snippet-button, .dialogue__button-secondary { z-index: 1; display: flex; align-items: center; justify-content: center; width: 100%; height: 40px; padding: 0px 16px; font-size: 12px; text-align: center; font-weight: 500; line-height: 38px; letter-spacing: 1px; text-transform: uppercase; text-decoration: none; white-space: nowrap; background-color: transparent; border-radius: 4px; border: 1px solid rgb(134, 134, 134); cursor: pointer; box-sizing: border-box; color: rgb(134, 134, 134) !important; }
.dialogue__button-secondary { transition: none 0s ease 0s; text-transform: none; border: 0px; margin: 16px auto 4px; letter-spacing: 0px; padding: 0px; height: auto; line-height: 14px; width: auto; }
.dialogue__snippet-button.dialogue__button-primary { background-color: rgb(25, 118, 210); border-color: rgb(25, 118, 210); font-weight: 700; box-shadow: rgba(0, 0, 0, 0.14) 0px 2px 2px 0px, rgba(0, 0, 0, 0.12) 0px 3px 1px -2px, rgba(0, 0, 0, 0.2) 0px 1px 5px 0px; color: rgb(255, 255, 255) !important; }
.dialogue__snippet-button.dialogue__button-primary:hover, .dialogue__snippet-button.dialogue__button-primary:focus { background-color: rgb(2, 92, 181); border-color: rgb(2, 92, 181); }
.dialogue__button-secondary:hover, .dialogue__button-secondary:focus { }
.dialogue__buttons { display: flex; flex-direction: column; margin-top: 16px; }
.dialogue__detect-browser { display: inline-block; width: 18px; height: 18px; margin: 0px 8px 0px 0px; }
.dialogue__detect-browser.dialogue__edge { background: url("data:image/png;base64,") 0% 0% / 100%; }
.dialogue__detect-browser.dialogue__chrome { background: url("data:image/png;base64,") 0% 0% / 100%; }
.dialogue__show-in { display: flex; }
.dialogue__fade-in { opacity: 1; transition-duration: 1s; }
@media (min-width: 1024px) {
  .dialogue__snippet { transform: translate3d(0px, 0px, 0px); }
}
