.my-mfp-zoom-in .mfp-content { opacity: 0; transition: all 0.2s ease-in-out 0s; transform: scale(0.8); }
.my-mfp-zoom-in.mfp-ready .mfp-content { opacity: 1; transition: all 0.2s ease-in-out 0s; transform: scale(1); }
.my-mfp-zoom-in.mfp-removing .mfp-content { opacity: 0; transition: all 0.2s ease-in-out 0s; transform: scale(0.8); }
.my-mfp-zoom-in.mfp-bg { opacity: 0; transition: opacity 0.2s ease-in-out 0s; }
.my-mfp-zoom-in.mfp-ready.mfp-bg { opacity: 0.84; transition: opacity 0.2s ease-in-out 0s; }
.my-mfp-zoom-in.mfp-removing.mfp-bg { opacity: 0; transition: opacity 0.2s ease-in-out 0s; }
#mfp-content { max-width: 750px; box-sizing: content-box; }
.frame-wrapper { position: absolute; display: block; top: -40px; left: 0px; width: 750px; height: 510px; box-shadow: rgba(0, 0, 0, 0.6) 0px 0px 8px; background: rgb(0, 0, 0); box-sizing: content-box; }
.frame-body { margin: 0px; width: 720px; height: 480px; padding: 15px; font-size: 13px; background: rgb(255, 255, 255); line-height: 18px; font-family: ff-meta-web-pro-n4, ff-meta-web-pro, arial, sans-serif; box-sizing: content-box; }
img { border: 0px; margin: 0px; padding: 0px; }
#splash-link { position: absolute; top: 0px; left: 0px; z-index: 10; width: 720px; height: 420px; display: block; }
#splash-image a, #splash-image img { width: 720px; height: 420px; display: block; }
#splash-links { width: 720px; height: 42px; padding: 15px 0px 0px; }
#splash-links a { float: left; height: 42px; display: block; font-size: 15px; font-weight: 400; line-height: 42px; text-decoration: none; font-family: ff-meta-web-pro-n4, ff-meta-web-pro, arial, sans-serif; }
#splash-links span { float: left; height: 42px; display: block; font-size: 15px; font-weight: 400; line-height: 42px; text-decoration: none; font-family: ff-meta-web-pro-n4, ff-meta-web-pro, arial, sans-serif; }
#splash-links span:hover { cursor: pointer; }
#splash-links .more { color: rgb(68, 68, 68); width: 238px; margin: 0px 0px 0px 20px; padding: 0px 0px 0px 12px; background: url("https://www.arsenal.com/sites/default/files/images/splash_arrow.jpg.jpeg") 0px 50% no-repeat; }
#splash-links .close { color: rgb(255, 255, 255); width: 418px; text-align: right; padding: 0px 36px 0px 0px; background: url("https://www.arsenal.com/sites/default/files/images/splash_button.png") right center no-repeat; box-sizing: content-box; }
